<script setup>
import {onBeforeUnmount, onMounted, ref} from 'vue';
import CameraMessage from "@/components/spy/CameraMessage.vue";

const arrow = ref(null);
let centerX = 0;
let centerY = 0;

function updateCenter() {
  const rect = arrow.value.getBoundingClientRect();
  centerX = rect.left + rect.width / 2;
  centerY = rect.top + rect.height / 2;
}

function onMouseMove(e) {
  const dx = e.clientX - centerX;
  const dy = e.clientY - centerY;
  const angle = Math.atan2(dy, dx) * (180 / Math.PI);
  arrow.value.style.transform = `rotate(${angle}deg)`;
}

onMounted(() => {
  updateCenter();
  window.addEventListener('mousemove', onMouseMove);
  window.addEventListener('resize', updateCenter);
});

onBeforeUnmount(() => {
  window.removeEventListener('mousemove', onMouseMove);
  window.removeEventListener('resize', updateCenter);
});
</script>

<template>
  <div class="camera-stand">
    <v-img
      width="80"
      src="data:image/png;base64,"
    />
  </div>

  <div ref="arrow" class="arrow">
    <v-img
      width="110"
      src="data:image/png;base64,"
    />

    <v-tooltip max-width="200" activator="parent">
      🔒 This camera is here to keep your data extra safe — don’t worry, we’ve got your back! 😊 SpyApp
    </v-tooltip>
  </div>

  <camera-message/>
</template>

<style scoped>
.camera-stand {
  position: fixed;
  top: 60px;
  right: 0;
  width: auto;
  height: auto;
  display: inline-block;
  cursor: default;
  user-select: none;
}

.arrow {
  position: fixed;
  top: 0px;
  right: 0px;
  rotate: 180deg;
  display: inline-block;
  transform-origin: bottom right;
  border-radius: 4px;
  cursor: default;
  user-select: none;
}
</style>
